// {APP_NAME} - YouTube Summarizer Frontend

import { useState } from 'react'
import './index.css'

function App() {
  const [url, setUrl] = useState('')
  const [summary, setSummary] = useState(null)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState('')

  const handleSummarize = async (e) => {
    e.preventDefault()
    if (!url.includes('youtube.com') && !url.includes('youtu.be')) {
      setError('Please enter a valid YouTube URL')
      return
    }

    setLoading(true)
    setError('')
    setSummary(null)

    try {
      const res = await fetch('http://localhost:8000/api/summarize', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url })
      })
      const data = await res.json()
      if (res.ok) {
        setSummary(data)
      } else {
        setError(data.detail || 'Failed to summarize video')
      }
    } catch (err) {
      setError('Network error. Please try again.')
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="app">
      <aside className="sidebar">
        <div className="sidebar-header">
          <h2>{APP_NAME}</h2>
          <p>Summarize YouTube videos instantly</p>
        </div>
        <div className="sidebar-nav">
          <div className="nav-section">
            <div className="nav-section-title">Features</div>
            <div className="nav-item">
              <span>üìù</span> AI-powered summaries
            </div>
            <div className="nav-item">
              <span>‚ö°</span> Lightning fast
            </div>
            <div className="nav-item">
              <span>üéØ</span> Key points extraction
            </div>
          </div>
        </div>
      </aside>

      <main className="main-content">
        <div className="hero">
          <h1>YouTube Video Summarizer</h1>
          <p className="hero-subtitle">Get concise summaries of any YouTube video in seconds</p>
        </div>

        <form onSubmit={handleSummarize} className="input-section">
          <input
            type="text"
            className="prompt-input"
            placeholder="Paste YouTube URL here..."
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            disabled={loading}
          />
          <button type="submit" className="generate-btn" disabled={loading || !url}>
            {loading ? (
              <><div className="spinner"></div> Summarizing...</>
            ) : (
              <>üìù Summarize Video</>
            )}
          </button>
        </form>

        {error && <div className="error-message">‚ö†Ô∏è {error}</div>}

        {summary && (
          <div className="result-card">
            <h3>{summary.title}</h3>
            <p className="video-info">Duration: {summary.duration} | Views: {summary.views}</p>
            <div className="summary-content">
              <h4>Summary</h4>
              <p>{summary.summary}</p>
              <h4>Key Points</h4>
              <ul>
                {summary.keyPoints.map((point, i) => (
                  <li key={i}>{point}</li>
                ))}
              </ul>
            </div>
          </div>
        )}
      </main>
    </div>
  )
}

export default App

