-- {APP_NAME} - Supabase Schema

CREATE TABLE summaries (
  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
  video_id TEXT NOT NULL UNIQUE,
  title TEXT NOT NULL,
  duration TEXT,
  views TEXT,
  summary TEXT NOT NULL,
  key_points JSONB,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

ALTER TABLE summaries ENABLE ROW LEVEL SECURITY;

CREATE POLICY "Enable read for all" ON summaries FOR SELECT USING (true);
CREATE POLICY "Enable insert for all" ON summaries FOR INSERT WITH CHECK (true);

CREATE INDEX idx_summaries_video_id ON summaries(video_id);
