// {APP_NAME} - Expense Tracker Frontend

import { useState, useEffect } from 'react'
import './index.css'

function App() {
  const [expenses, setExpenses] = useState([])
  const [amount, setAmount] = useState('')
  const [category, setCategory] = useState('food')
  const [description, setDescription] = useState('')

  useEffect(() => {
    fetchExpenses()
  }, [])

  const fetchExpenses = async () => {
    const res = await fetch('http://localhost:8000/api/expenses')
    const data = await res.json()
    setExpenses(data)
  }

  const addExpense = async (e) => {
    e.preventDefault()
    const res = await fetch('http://localhost:8000/api/expenses', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ amount: parseFloat(amount), category, description })
    })
    const expense = await res.json()
    setExpenses([...expenses, expense])
    setAmount('')
    setDescription('')
  }

  const total = expenses.reduce((sum, e) => sum + e.amount, 0)

  return (
    <div className="app">
      <aside className="sidebar">
        <div className="sidebar-header">
          <h2>{APP_NAME}</h2>
          <p>Track your spending</p>
        </div>
        <div className="sidebar-nav">
          <div className="nav-section">
            <div className="nav-section-title">Categories</div>
            <div className="nav-item"><span>üçî</span> Food</div>
            <div className="nav-item"><span>üöó</span> Transport</div>
            <div className="nav-item"><span>üè†</span> Bills</div>
            <div className="nav-item"><span>üéâ</span> Entertainment</div>
          </div>
        </div>
        <div className="sidebar-footer">
          <div className="info-box">
            <strong>${total.toFixed(2)}</strong>
            <p>total spent</p>
          </div>
        </div>
      </aside>

      <main className="main-content">
        <form onSubmit={addExpense} className="expense-form">
          <input
            type="number"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            step="0.01"
            required
          />
          <select value={category} onChange={(e) => setCategory(e.target.value)}>
            <option value="food">Food</option>
            <option value="transport">Transport</option>
            <option value="bills">Bills</option>
            <option value="entertainment">Entertainment</option>
          </select>
          <input
            type="text"
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <button type="submit">Add Expense</button>
        </form>

        <div className="expenses-list">
          {expenses.map(expense => (
            <div key={expense.id} className="expense-card">
              <span className="expense-amount">${expense.amount}</span>
              <span className="expense-category">{expense.category}</span>
              <span className="expense-desc">{expense.description}</span>
            </div>
          ))}
        </div>
      </main>
    </div>
  )
}

export default App

