// {APP_NAME} - Frontend (React + Vite)

// ===== src/App.jsx =====
import { useState, useEffect } from 'react'
import './index.css'

function App() {
  const [tasks, setTasks] = useState([])
  const [newTask, setNewTask] = useState('')
  const [filter, setFilter] = useState('all')

  useEffect(() => {
    fetchTasks()
  }, [])

  const fetchTasks = async () => {
    const res = await fetch('http://localhost:8000/api/tasks')
    const data = await res.json()
    setTasks(data)
  }

  const addTask = async (e) => {
    e.preventDefault()
    if (!newTask.trim()) return

    const res = await fetch('http://localhost:8000/api/tasks', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ title: newTask })
    })
    const task = await res.json()
    setTasks([...tasks, task])
    setNewTask('')
  }

  const toggleTask = async (id) => {
    const task = tasks.find(t => t.id === id)
    const res = await fetch(`http://localhost:8000/api/tasks/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ completed: !task.completed })
    })
    const updated = await res.json()
    setTasks(tasks.map(t => t.id === id ? updated : t))
  }

  const deleteTask = async (id) => {
    await fetch(`http://localhost:8000/api/tasks/${id}`, { method: 'DELETE' })
    setTasks(tasks.filter(t => t.id !== id))
  }

  const filteredTasks = tasks.filter(task => {
    if (filter === 'active') return !task.completed
    if (filter === 'completed') return task.completed
    return true
  })

  return (
    <div className="app">
      <aside className="sidebar">
        <div className="sidebar-header">
          <h2>{APP_NAME}</h2>
          <p>Stay organized, get things done</p>
        </div>
        <nav className="sidebar-nav">
          <div className="nav-section">
            <div className="nav-section-title">Filters</div>
            <button className={`nav-item ${filter === 'all' ? 'active' : ''}`} onClick={() => setFilter('all')}>
              <span>üìã</span> All Tasks
            </button>
            <button className={`nav-item ${filter === 'active' ? 'active' : ''}`} onClick={() => setFilter('active')}>
              <span>‚è≥</span> Active
            </button>
            <button className={`nav-item ${filter === 'completed' ? 'active' : ''}`} onClick={() => setFilter('completed')}>
              <span>‚úÖ</span> Completed
            </button>
          </div>
        </nav>
        <div className="sidebar-footer">
          <div className="info-box">
            <strong>{tasks.filter(t => !t.completed).length}</strong>
            <p>tasks remaining</p>
          </div>
        </div>
      </aside>

      <main className="main-content">
        <form onSubmit={addTask} className="add-task-form">
          <input
            type="text"
            placeholder="Add a new task..."
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
          />
          <button type="submit">Add Task</button>
        </form>

        <div className="tasks-list">
          {filteredTasks.map(task => (
            <div key={task.id} className={`task-card ${task.completed ? 'completed' : ''}`}>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => toggleTask(task.id)}
              />
              <span>{task.title}</span>
              <button className="delete-btn" onClick={() => deleteTask(task.id)}>√ó</button>
            </div>
          ))}
        </div>
      </main>
    </div>
  )
}

export default App

